// instalar redux - npm i redux react-redux
// Uma action é um objeto JavaScript que tem pelo menos uma propriedade type e é responsável por comunicar ao reducer
// reducer é uma função// retorno do reduce é um novo estado
// reduce tem 2 parametro(stado, action)
// dispatch é uma função que recebe como parametro um objeto(action)
// uma mudança a ser feita na store. (action tem propriedade type)
import { createStore } from 'redux';
import { composeWithDevTools } from 'redux-devtools-extension';
import { combineReducers } from 'redux';
import Data from 'data';

// pega uma lista de api
const INICIAL_STATE = {
    categoria: Data,
}

function reducerUser(state={}, action){
    return state;
}

function reducerPagamento(state={}, action){
    return state;
}

const rootReducer = combineReducers({
    reducerUser,
    reducerPagamento,
})

const store = createStore(rootReducer, composeWithDevTools);



export default store;